import React from "react";
import ReactDOM from "react-dom";
import { useState } from 'react';
function Calculator (){
  let [pantalla,setPantalla] = useState(0)
  let [resultado,setResultado] = useState(0)

  let valor1 = (evento.target.value);

  function uno (evento){
    setPantalla(evento.target.value);

  };
  function dos (evento){
    setPantalla(evento.target.value);

  };
  function suma (evento){
    setResultado(parseInt(uno)+ parseInt(dos))
  }
    return (
      <div>
        <input type="button" value={1} onClick={uno}></input>
        <input type="button" value={2} onClick={dos}></input>
        <button value={'+'} onClick={suma}>+</button>
        <h1>Resultado={valor1}</h1>
      </div>
    )
  }
const rootElement = document.getElementById("root");
ReactDOM.render(<Calculator />, rootElement);
