import React, { useState } from "react";
import ReactDOM from "react-dom";

function CalculadoraPro(){
  let bt1 = 1;
  let bt2 = 2;
  const [num1,setNum1] = useState(0);
  const [resultado,setResultado] = useState(0)

  const suma = () =>{
      setResultado(num1 + num2)
  };
return(
  <div>
    <button type="number" value={num1} onClick={(e)=>setNum1(parseInt(e.num1.target.value))}>1</button>
    <button type="number" value={num2} onClick={(e)=>setNum1(parseInt(e.num1.target.value))}>2</button>

    <button onClick={suma}>+</button>
    <p>Resultado:{ resultado}</p>
  </div>
)
}
const rootElement = document.getElementById("root");
ReactDOM.render(<CalculadoraPro/>, rootElement);
