import React, { useState } from "react";
import ReactDOM from "react-dom";
import {} from "./estilos.css";

function CalculadoraPro(){
const [num1,setNum1] = useState(0)
const [num2,setNum2] = useState(0)
const sumar = (parseInt(num1), parseInt(num2));

return(
  <div>
    <form 
        onSubmit={ev => {
        ev.preventDefault();
        setNum1(ev.target.num1.value);
        setNum2(ev.target.num2.value)
        }}

    > 
        <button name="num1" value={1}>1</button>
        <button name="num2" value={2}>2</button>

        <button type="submit">Sumar</button>
        <p>{sumar}</p>
    </form>
  </div>
)
}
const rootElement = document.getElementById("root");
ReactDOM.render(<CalculadoraPro/>, rootElement);
