import React from "react";
import ReactDOM from "react-dom";

import { useState } from 'react';

function Perritos() {
  const [perritos, setPerritos] = useState(0);
  const [menosperritos, setMenosperritos] = useState(0);

  //Creamos una funcion para hacer un contador
  function contarperritos(){
    setPerritos (perritos + 1)
  }

  function restarperritos(){
    setPerritos (perritos - 1)
  }

  return (
    <div>
        <h1>Numero de Perritos</h1>
        <h1>{perritos}</h1>
        <button onClick={contarperritos}>
            Aumentar Perritos
        </button>
        <br />
        <br />
        <button onClick={restarperritos}>
            disminuir Perritos
        </button>
    </div>
  );
}


const rootElement = document.getElementById("root");
ReactDOM.render(<Perritos />, rootElement);
