import React from "react";
import ReactDOM from "react-dom";

import { useState } from 'react';

function Calculator() {
  const [num1, setValor1] = useState(0);
  const [num2, setValor2] = useState(0);
  const [resultado, setResultado] = useState(0);

  const suma = () => {
    setResultado(num1 + num2);
  }

  const resta = () => {
    setResultado(num1 - num2);
  }

  const multiplicacion = () => {
    setResultado(num1 * num2);
  }

  const division = () => {
    setResultado(num1 / num2);
  }

  return (
    <div>
      <h1>Calculadora</h1>
      <input type="number" value={num1} onChange={(e) => setValor1(parseInt(e.target.value))} />
      <input type="number" value={num2} onChange={(e) => setValor2(parseInt(e.target.value))} />
      <button onClick={suma}>+</button>
      <button onClick={resta}>-</button>
      <button onClick={multiplicacion}>*</button>
      <button onClick={division}>/</button>
      <p>Total: {resultado}</p>
    </div>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<Calculator />, rootElement);
