import React from "react";
import ReactDOM from "react-dom";
import { useState } from 'react';
function Calculator (){
  let [valor,setValor] = useState("");
  let [valor2,setValor2] = useState("");
  let [suma,setSuma] = useState("");
  let [pantalla,setPantalla] = useState(0)

  function uno (evento){
    setValor(evento.target.value);

  };
  function dos (evento){
    setValor2(evento.target.value);

  };
  function sum (evento){
    setSuma(evento.target.value);

  };
  function cuadro (){
    setPantalla(parseInt(valor) + parseInt(valor2))
  }
    return (
      <div>
        <input type="button" value={1} onClick={uno}></input>
        <input type="button" value={2} onClick={dos}></input>
        <input type="button" value={'+'} onClick={sum}></input>
        <h1>Resultado={pantalla}</h1>
      </div>
    )
  }
const rootElement = document.getElementById("root");
ReactDOM.render(<Calculator />, rootElement);
