return(
  <div>
    <h3>El numero que elijas se mostrara en resultado</h3>

    <button type="number" value={num1} onClick={(e)=>setNum1(parseInt(e.num1.target.value))}>1</button>
    <button type="number" value={num1} onClick={(e)=>setNum1(parseInt(e.num1.target.value))}>2</button>

    <p>Resultado:{num1}</p>
  </div>
)
}
