import React, { useState } from "react";
import ReactDOM from "react-dom";

function CalculadoraPro(){
  let bt1 = 1;
  let bt2 = 2;
  const [num1,setNum1] = useState(0);
  const [num2,setNum2] = useState(0);

return(
  <div>
    <button type="number" value={num1} onClick={(e)=>setNum1(parseInt(e.num1.target.value))}>{bt1}</button>
    <button type="number" value={num2} onClick={(e)=>setNum2(parseInt(e.num1.target.value))}>{bt2}</button>
  </div>
)
}
const rootElement = document.getElementById("root");
ReactDOM.render(<CalculadoraPro/>, rootElement);
